uint64_t hash(bitset<722> board)
{
    uint64_t result = 0;
	uint64_t mask = 1;

    for (int chunk = 0; chunk < 14; chunk++)
    {;
        for (int i = 0; i < 64; i++)
		{
			if (board[i])
				result ^= mask;
			mask <<= 1;
		}
		result *= (chunk + 1);
		// cout << result << "   " << endl;
        board >>= 64;
		mask = 1;
		// cout << board << endl;
    }
    return result;
}



